// * the LICENSE file in the top-level directory.

// */


import "hardhat/console.sol";
import "./Agreement.sol";


contract ExplicitUserConsent {
    
    modifier onlyOwner(string memory _dataOfferingId, string memory _consentSubject) {
    require(msg.sender == consents[_dataOfferingId][_consentSubject].consentOperator, "Only the owner can revoke the consent.");
    _;
    }
    
    //The owner of the consent and the actual entity/subject may be different.
    //Well-being pilot, delegated. The owner: provider, subject: owner ->what identity if he is not digitally native?
    //Different data owners with different addresses
    //That's why we store both.
    //One is the one who can operate the consent and the other one is the identifier of the actual data owner
    
    //location: within the country, within EU, other continent
    //purpose

    struct Consent {
        string dataOfferingId;
        string consentSubject;
        address consentOperator;
        string consentFormHash;
        uint256 startDate; 
        uint256 endDate;
        ConsentStatus status;
    }

    enum ConsentStatus {
        Given, 
        Revoked
    }

    mapping (string => Consent[]) offeringToConsents;

    //mapping(address => mapping (string => Consent)) public consents;
    mapping(string => mapping (string => Consent)) consents;

    event RevokeConsent(string[], string);

    function giveConsent( string memory _dataOfferingId, string memory _consentSubject, string memory _consentFormHash, 
                            uint256 startDate, uint256 endDate) public {
                            
        Consent memory consent;
        consent.dataOfferingId = _dataOfferingId;
        consent.consentSubject = _consentSubject;
        consent.consentOperator = msg.sender;
        consent.consentFormHash = _consentFormHash;
        consent.startDate = startDate;
        consent.endDate = endDate;
        consent.status = ConsentStatus.Given; //status

        offeringToConsents[_dataOfferingId].push(consent); 
        consents[_dataOfferingId][_consentSubject] = consent;
    }

    // check all based on data offering -> return array
    // check status for just one consent based on the consent subject -> return status

     function checkConsentStatusByOffering(string memory _dataOfferingId) external view returns (ConsentStatus[] memory) {
        Consent[] memory consentsByDataOffering = offeringToConsents[_dataOfferingId];
        uint length = consentsByDataOffering.length;
        ConsentStatus[] memory consentsStatus = new ConsentStatus[](length);
        uint256 k = 0;
        for(uint256 i=0; i < length; i++){
            if(block.timestamp > consentsByDataOffering[i].endDate)
                consentsByDataOffering[i].status = ConsentStatus.Revoked;
            consentsStatus[k++] = consentsByDataOffering[i].status;
        }
        return consentsStatus;
    }

    function checkConsentStatusByConsentSubject(string memory _dataOfferingId, string memory _consentSubject) external view returns (ConsentStatus) {
        Consent memory consent = consents[_dataOfferingId][_consentSubject];
        if(block.timestamp > consent.endDate)
                //revokeConsent(_addr, _dataOfferingId, _consentSubject);
                consent.status = ConsentStatus.Revoked;
                //consents[_dataOfferingId][_consentSubject].status = ConsentStatus.Revoked;
        return consent.status;
    }
    //send state and if it revoked call revoke endpoint
    
    //if consent subject empty then revoke all consents
    //require in function
    //revoke consent where owner==msg.sender
    function revokeConsent(address _addr, string memory _dataOfferingId, string memory _consentSubject) public{ //onlyOwner(_dataOfferingId, _consentSubject){
        //check whether it is the consent owner 

        bytes memory emptyString = bytes(_consentSubject); 
        if (emptyString.length == 0) {
            //revoke for all consent subjects
             //require(offeringToConsents[_dataOfferingId][0].owner == msg.sender);
            Consent[] memory consentsByDataOffering = offeringToConsents[_dataOfferingId];
            uint length = consentsByDataOffering.length;
            for(uint256 i=0; i < length; i++){
                    offeringToConsents[_dataOfferingId][i].status = ConsentStatus.Revoked;
             }   
        } else {
            //should update both consent mapping
             require(consents[_dataOfferingId][_consentSubject].consentOperator == msg.sender);
        
            consents[_dataOfferingId][_consentSubject].status = ConsentStatus.Revoked; 
        }
       
        DataSharingAgreement agreement = DataSharingAgreement(_addr);

        string[] memory consumers = agreement.notifyConsentRevoked(_dataOfferingId);

        emit RevokeConsent(consumers, _dataOfferingId);
    }
}
